#!/bin/bash# This script is used by Render to start the application# Install bleach explicitly (to fix the issue with missing bleach module)pip install bleach# Set log level for better debuggingexport LOG_LEVEL=INFO# Print environment configuration (without sensitive values)echo "Starting server with the following configuration:"echo "ENVIRONMENT: $ENVIRONMENT"echo "DB_NAME: $DB_NAME"echo "MongoDB URL: [Configured]" echo "Gemini API: [Configured]"echo "CORS Origins: $CORS_ORIGINS"# Check if Gemini API key is setif [ -z "$GEMINI_API_KEY" ]; then    echo "Warning: GEMINI_API_KEY is not set. AI features will be disabled."fi# Check if MongoDB URL is setif [ -z "$MONGO_URL" ]; then    echo "Warning: MONGO_URL is not set. Database features will be disabled."fi# Start the serverecho "Starting server..."python server.py